from flask import render_templatefrom flask.views import MethodViewimport gbmodelclass ViewSongs(MethodView):    def get(self):        model = gbmodel.get_model()        # Fetch songs and store them as dictionaries, placing id last        songs = [dict(title=row[0], artist=row[1], genre=row[2], release_date=row[3], lyrics=row[4], rating=row[5], id=row[6])                  for row in model.get_all_songs()]        return render_template('view.html', songs=songs)